con:config.script:
log.info("Chargement des propriétés à partir du fichier .csv")
def csvpath = "C:/JddLibreplan.csv"
File file = new File(csvpath)
def line1 = file.readLines().get(1)
def line2 = file.readLines().get(2)

String[] rowdata1 = line1.split(";")
String[] rowdata2 = line2.split(";")

def var = rowdata1.toString()
int longueur = var.split(",").size()

for(int i=0;i<longueur;i++) {
String Name = rowdata1[i];
Value = rowdata2[i];
testRunner.testCase.getTestStepByName("Properties").setPropertyValue(Name,Value)
}

